import React, { Component } from "react";
import Nav from "../components/Nav";
import Title from "../components/Title";
import Header from "../components/Header";
import Container from "../components/Container";
import API from "../utils/API";
import "../map.css";

class List extends Component {

    state = {
        page: "list",
        plants: [],
        title: "Scientific Names by Genus",
        subtitle: "Results",
        header: "Hi - ",
        letter: ""
      };

      componentDidMount() {
        const {selection} = this.props.match
        console.log(selection + "selection here");
        // this.setState({letter: selection})
    
        // fetch(`localhost:3000/list/genus/${letter}`)
        //   .then((selection) => {
        //     this.setState(() => ({ selection }))
        //   });
        this.loadPlants(selection);
      }

      loadPlants = (letter) => {
        console.log(letter + "inside loadPlants()");
        // let letter = this.state.letter;
        API.getGenus(letter)
          .then(res => {
            console.log(res);
            this.setState({ plants: res.data});
          }
          )
          .catch(err => console.log(err));
      };

    render () {
        return (
            <div>
                <Nav setSelection={this.setSelection}/>
                <Title 
                    title={this.state.title}
                    subtitle={this.state.subtitle}
                    />
                <hr />
                <Header 
                  header={this.state.header}
                  selection={this.state.selection}
                  />

                <Container 
                  plants={this.state.plants}
                  page={this.state.page}
                  />
                
            </div>
        );
    }
}

export default List;
