import React from "react";
// import Flatlist from "../Flatlist";
import "../../map.css";
import "./style.css";

const Hero = (props) => {
   const {plants}=props;
   const {page}=props;
        {
         switch(page){
            case "home":
               return (
                        <div className="hero">
                           <div className="hero-image">
                              <p className="hero-text">Texas Mountain Laurel (<span className="scientific-name">Sophora secundiflora
                                 </span>)
                              </p>
                           </div>
                        </div>
                  );
            case "list":
                return (
                  <div className="item-list">
                     <ul>
                        {plants.map(plant => (
                           <li key={plant._id}>
                                 <a className="profile-link" href={"/profile/" + plant._id}>
                                 <span className="scientific-name">{plant.scientific_name}</span> -
                                 <span>{plant.common_names}</span>
                                 </a>
                           </li>
                        ))}
                    </ul>
      
              </div>
                );
            case "profile":
              return (
                     <div id="hero">
                     </div>
               );
               } 
            
         }
};
      
export default Hero;
